<!-- 仪表盘组件 -->
<template>
  <div :style="{'width': width + 'px','height': height +'px'}">
    <img
      ref="canvasImg"
      :src="image || 'data:image/png;base64,'"
      style="position: absolute; top: -9999px; z-index: -9999999"
    />
    <canvas ref="canvasBox" :style="{'width': width + 'px','height': height +'px'}"></canvas>
    <slot name="dasht"></slot>
  </div>
</template>

<script>
/**
 * 绘制图案样式
 * @param index 绘制的数量
 * @param color 颜色
 * @param opacity 透明度
 * @param deg 绘制弧长
 * @param type 类型风格
 *        arc: 圆圈
 *        link: 线条
 *        default: 默认芝麻信用风格
 */
function setColorTick() {
  this.draw = ctx => {
    let img = document.getElementById('canvasImg')
    ctx.save()
    for (let i = 0; i <= this.index; i++) {
      ctx.beginPath()
      ctx.strokeStyle = this.color
      ctx.fillStyle = this.color
      switch (this.type) {
        case 'arc':
          ctx.arc(this.width - 18, 6, 4, 0, Math.PI * 2, true)
          ctx.fill()
          break
        case 'link':
          ctx.lineWidth = 2
          ctx.moveTo(this.width - 8, 0)
          ctx.lineTo(this.width - 18, 0)
          break
        default:
          ctx.globalAlpha = this.opacity
          ctx.drawImage(img, this.width - 18, 0, 10, 6)
          break
      }
      ctx.stroke()
      ctx.rotate(this.deg)
    }
    ctx.restore()
  }
}

export default {
  name: 'dash-board',
  props: {
    width: {
      type: Number,
      default: 300
    },
    height: {
      type: Number,
      default: 300
    },
    // 总分
    total: {
      type: Number,
      default: 900
    },
    // 当前分
    current: {
      type: Number,
      default: 200
    },
    // 刻度数量
    score: {
      type: Number,
      default: 24
    },
    // 越大越快
    speed: {
      type: Number,
      default: 8
    },
    // 颜色，type为圆或者线生效
    color: {
      type: String,
      default: '#fff'
    },
    bg: {
      type: String,
      default: 'rgba(255, 255, 255, .3)'
    },
    // type为图片生效
    image: {
      type: String
    },
    type: {
      type: String,
      default: ''
    },
    // 顶上标题
    promptitle: {
      type: String,
      default: ''
    },
    // 状态标题
    statetitle: {
      type: String,
      default: ''
    }
  },
  data() {
    return {}
  },
  watch: {
    current(){
      this.initGauge()
    }
  },
  computed: {
    /**
     * deg1() 计算弧长
     * 弧长计算公式是一个数学公式，为L=n（圆心角度数）× π（1）× r（半径）/180（角度制），L=α（弧度）× r(半径) （弧度制）。其中n是圆心角度数，r是半径，L是圆心角弧长。
     * 整个运动的角度是（360-120）度，转换成弧度就是12π/9，一共分成了50个分数段，那么每一个分数段就是12π/450 = 2π / 75
     * 如需旋转 5 度，可规定下面的公式：5*Math.PI/180。
     */
    degScore() {
      return (Math.PI * 13) / (9 * this.score)
    }
  },
  methods: {
    // 解决手机下模糊问题
    setupCanvas(canvas) {
      // Get the device pixel ratio, falling back to 1.
      let dpr = window.devicePixelRatio || 1;
      // Get the size of the canvas in CSS pixels.
      let rect = canvas.getBoundingClientRect();
      // Give the canvas pixel dimensions of their CSS
      // size * the device pixel ratio.
      canvas.width = rect.width * dpr;
      canvas.height = rect.height * dpr;
      let ctx = canvas.getContext('2d');
      // Scale all drawing operations by the dpr, so you
      // don't have to worry about the difference.
      ctx.scale(dpr, dpr);
      return ctx;
    },
    /**
     * [drawText 绘制文字]
     * @param ctx 画布对象
     * @param process 信用分
     * @return
     */
    drawText(ctx, process) {
      // 旋转，使文字角度正确
      ctx.save()
      ctx.rotate((220 * Math.PI) / 180)
      // 绘制'信用分'
      ctx.fillStyle = '#fff'
      ctx.font = '12px Microsoft yahei'
      ctx.textAlign = 'center'
      ctx.textBaseLine = 'top'
      ctx.fillText(this.promptitle, 0, -30)
      // 绘制数值
      ctx.fillStyle = '#fff'
      ctx.font = '46px Microsoft yahei'
      ctx.textAlign = 'center'
      ctx.textBaseLine = 'top'
      ctx.fillText(process, 0, 20)
      // 绘制数值
      ctx.fillStyle = '#fff'
      ctx.font = '14px Microsoft yahei'
      ctx.textAlign = 'center'
      ctx.textBaseLine = 'top'
      ctx.fillText(this.statetitle, 0, 46)
      ctx.restore()
    },
    /**
     * [drawRate 根据分数评级]
     * @param num 数值
     * @return
     */
    drawRate(num = 300) {
      let str = ''
      if (num < 300) {
        str = '极差'
      } else if (num < 500) {
        str = '中等'
      } else if (num < 650) {
        str = '良好'
      } else if (num < 800) {
        str = '优秀'
      } else {
        str = '极好'
      }
      return str
    },
    /**
     * [initGauge 初始化]
     * @return
     */
    initGauge() {
      const ctx = this.setupCanvas(this.$refs.canvasBox)
      const that = this
      const [cWidth, cHeight, score, deg] = [this.width, this.height, this.score, this.degScore]
      // 数字增加速度
      let dotSpeed = this.speed / 100
      // 内环动点旋转角度
      let textSpeed = Math.round((dotSpeed * 2) / deg)
      // 起始分数,数字递增用
      let credit = 0
      let colorTick = new setColorTick()
      let colorIndex = 0
      // 动点旋转速度 / 弧度
      let colorSpeed = dotSpeed / deg
      // 当前刻度值 = （当前分 / 总分）* 总刻度
      let currSpeed = Math.round((this.current / this.total) * this.score)
      ;(function drawFrame() {
        ctx.save()
        ctx.clearRect(0, 0, cWidth, cHeight)
        ctx.translate(cWidth / 2, cHeight / 2)
        // 因圆本身缺口为110°,为了让缺口朝正下方,所以旋转角度为140°
        ctx.rotate((140 * Math.PI) / 180)
        // 累计动效
        if (credit < score - textSpeed) {
          that.current === 0 ? 0 : credit += textSpeed
        } else if (credit >= score - textSpeed && credit < score) {
          // 显示最终结果
          credit = that.current
        }
        that.drawText(ctx, credit)
        ctx.save()
        // 绘制全部刻度
        colorTick.color = that.bg
        colorTick.deg = deg
        colorTick.index = score
        colorTick.opacity = 0.4
        colorTick.width = that.width / 2
        colorTick.type = that.type
        colorTick.draw(ctx)
        ctx.restore()
        // 绘制实际刻度
        if (colorIndex < currSpeed && colorIndex <= score) {
          colorIndex += colorSpeed
        }
        colorTick.color = that.color
        colorTick.deg = deg
        colorTick.index = colorIndex
        colorTick.opacity = 1
        colorTick.width = that.width / 2
        colorTick.type = that.type
        colorTick.draw(ctx)
        window.requestAnimationFrame(drawFrame)
        ctx.restore()
      })()
    }
  },
  mounted() {
    this.$nextTick(() => {
      this.initGauge()
    })
  }
};
</script>
